create table PUBLISHERAPP.ADDRESS (id bigint generated by default as identity (start with 1), address1 varchar(255) not null, address2 varchar(255), city varchar(255), state varchar(2), version integer, zip_code varchar(255), primary key (id));
create table PUBLISHERAPP.ARTICLE (id bigint generated by default as identity (start with 1), no_of_pages integer, title varchar(255) not null, version integer, magazine_id bigint not null, primary key (id), unique (title));
create table PUBLISHERAPP.ARTICLE_AUTHOR (article_id bigint not null, author_id bigint not null);
create table PUBLISHERAPP.AUTHOR (id bigint generated by default as identity (start with 1), first_name varchar(255), last_name varchar(255) not null, version integer, primary key (id), unique (first_name, last_name));
create table PUBLISHERAPP.BOOK (BOOK_TYPE varchar(31) not null, id bigint generated by default as identity (start with 1), name varchar(255) not null, price double, publish_date timestamp, version integer, isbn varchar(255), cover_image_file varbinary(255), file_Name varchar(255), primary key (id));
create table PUBLISHERAPP.BOOK_AUTHOR (book_id bigint not null, author_id bigint not null, primary key (book_id, author_id));
create table PUBLISHERAPP.CHAPTER (id bigint generated by default as identity (start with 1), no_of_pages integer, title varchar(255) not null, version integer, novel_id bigint not null, primary key (id), unique (title));
create table PUBLISHERAPP.MAGAZINE (id bigint generated by default as identity (start with 1), av_online bit, magazine_name varchar(255) not null, price double, publish_date timestamp not null, version integer, primary key (id));
create table PUBLISHERAPP.OFFICE (id bigint generated by default as identity (start with 1), version integer, weekday_contact_emailAddress varchar(255), weekday_contact_phone varchar(255), weekday_contact_recipient varchar(255), weekend_contact_emailAddress varchar(255), weekend_contact_phone varchar(255), weekend_contact_recipient varchar(255), address_id bigint, primary key (id));
